import React, { useState } from 'react';
import {
    Button,
    Grid,
    InputAdornment,
    TextField,
    FormControlLabel,
    Checkbox,
    Typography,
} from '@material-ui/core';
import { AccountCircle, LockRounded } from '@material-ui/icons';
import { Form, Formik, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import {
    Link,
    useHistory,
} from 'react-router-dom';
import * as apiUtils from '../../../../utils/apiUtils';
import './LoginForm.scss';
import WarningMessage from '../WarningMessage/WarningMessage';

const LoginForm = () => {
    const history = useHistory();
    const [warning, setWarning] = useState({
      shown: false,
      message: '',
    });

    const initialValues = {
      email: '',
      password: '',
    };
    const validationSchema = Yup.object().shape({
      email: Yup.string().email('Please enter valid email').required('Email Required!'),
      password: Yup.string().required('Password Required!'),
    });
    const onSubmit = async ({ email, password }) => {
      setWarning({
        shown: false,
        message: '',
      });
      try {
        const loginResponse = await apiUtils.login({ email, password });
        const statusResponse = await apiUtils.verifyStatus(email);
        const companyResponse = await apiUtils.verifyCompany(email);

        if (loginResponse.status === 200 && statusResponse.status === 200) {
          localStorage.setItem('email', email);
          localStorage.setItem('login', 'true');
          localStorage.setItem('userId', loginResponse.data.id);
          localStorage.setItem('name', loginResponse.data.name);

          if (companyResponse.status === 200) {
            localStorage.setItem('companyId', companyResponse.data);
            history.push('/dashboard');
          }
          if (companyResponse.status === 204) {
            history.push('/firstlogin');
          }
        }

      } catch (error) {
        if (error.response.status >= 500) {
          setWarning({
            shown: true,
            message: "INTERNAL_SERVER_ERROR",
          });
        }
        if (error.response.status >= 400 && error.response.status < 500) {
          setWarning({
            shown: true,
            message: "USERNAME_PASSWORD_MISMATCH_ERROR",
          });
        }
      }
    };
    return (
      <div className="login_form">
        <Formik
          initialValues={initialValues}
          onSubmit={onSubmit}
          validationSchema={validationSchema}
        >
          {({ isSubmitting }) => (
            <Form>
              <div>
                {warning.shown ? <WarningMessage content={warning.message} /> : null}
              </div>
              <div>
              </div>
              <Field
                as={TextField}
                label="Email"
                name="email"
                variant="outlined"
                margin="normal"
                fullWidth
                id="email"
                autoFocus
                autoComplete="email"
                helperText={(
                  <ErrorMessage name="email">
                    { (msg) => <span className="errorMessage">{msg}</span>}
                  </ErrorMessage>
                )}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <AccountCircle />
                    </InputAdornment>
                  ),
                }}
              />
              <Field
                data-testid="passwordInput"
                as={TextField}
                label="Password"
                variant="outlined"
                margin="normal"
                fullWidth
                id="password"
                type="password"
                name="password"
                autoComplete="current-password"
                helperText={(
                  <ErrorMessage name="password">
                    { (msg) => <span className="errorMessage">{msg}</span>}
                  </ErrorMessage>
                )}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <LockRounded />
                    </InputAdornment>
                  ),
                }}
              />
              <div className="remember">
                <Field
                  as={FormControlLabel}
                  control={<Checkbox value="remember" style={{ color: '#2ab782' }} />}
                  label="Remember me"
                  name="remember"
                />
              </div>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                style={{ backgroundColor: '#2ab782' }}
                disabled={isSubmitting}
              >
                Sign in
              </Button>
              <div style={{ height: 10 }} />
              <Grid container direction="row" justify="space-between" alignItems="center">
                <Grid item>
                  <Typography>
                    <Link className="link" to="/">
                      Forgot Password?
                    </Link>
                  </Typography>
                </Grid>
                <Grid item>
                  <Typography>
                    New User? &nbsp;
                    <Link id="signUpLink" className="link" to="/signup">
                      Sign Up
                    </Link>
                  </Typography>
                </Grid>
              </Grid>
            </Form>
          )}
        </Formik>
      </div>
    );
  };

  export default LoginForm;
